  <main class="container" role="main" aria-label="Email capture form">
    <h1>Develop Email Capture Form</h1>
    <p class="description">Implement a form to collect visitor email addresses securely.</p>
    <form id="emailForm" novalidate autocomplete="off" aria-describedby="error-msg">
      <label for="emailInput">Email address</label>
      <input
        type="email"
        id="emailInput"
        name="email"
        placeholder="Enter your email address"
        aria-required="true"
        aria-invalid="false"
        aria-describedby="error-msg"
        required
        autocomplete="email"
      />
      <div id="error-msg" class="error-message" role="alert" aria-live="assertive"></div>
      <button type="submit" id="submitBtn">Subscribe</button>
    </form>
    <div id="successMessage" class="success-message" role="alert" aria-live="polite" style="display:none;">
      Thank you for subscribing!
    </div>
  </main>

<script>
  (function() {
    const form = document.getElementById('emailForm');
    const emailInput = document.getElementById('emailInput');
    const errorMsg = document.getElementById('error-msg');
    const submitBtn = document.getElementById('submitBtn');
    const successMessage = document.getElementById('successMessage');

    // Simple email regex (RFC 5322 simplified)
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    function validateEmail(email) {
      return emailRegex.test(email.trim());
    }

    function setError(message) {
      errorMsg.textContent = message;
      emailInput.classList.add('error');
      emailInput.setAttribute('aria-invalid', 'true');
    }

    function clearError() {
      errorMsg.textContent = '';
      emailInput.classList.remove('error');
      emailInput.setAttribute('aria-invalid', 'false');
    }

    function disableForm(disabled = true) {
      emailInput.disabled = disabled;
      submitBtn.disabled = disabled;
    }

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      clearError();

      const emailValue = emailInput.value;

      if (!emailValue) {
        setError('Email address is required.');
        emailInput.focus();
        return;
      }

      if (!validateEmail(emailValue)) {
        setError('Please enter a valid email address.');
        emailInput.focus();
        return;
      }

      // Simulate secure submission (e.g. to backend)
      disableForm(true);
      submitBtn.textContent = 'Submitting...';

      // Fake async submission
      setTimeout(() => {
        form.style.display = 'none';
        successMessage.style.display = 'block';
      }, 1500);
    });

    emailInput.addEventListener('input', () => {
      if (errorMsg.textContent) {
        clearError();
      }
    });
  })();
</script>
</body>
</html>
