<script>
  (function() {
    const form = document.getElementById('emailForm');
    const emailInput = document.getElementById('emailInput');
    const messageDiv = document.getElementById('formMessage');
    const button = form.querySelector('button');

    // Replace this URL with your email marketing platform endpoint
    const API_ENDPOINT = 'https://example.com/api/subscribe';

    function setMessage(text, type = '') {
      messageDiv.textContent = text;
      messageDiv.className = 'message';
      if (type === 'success') {
        messageDiv.classList.add('success');
      } else if (type === 'error') {
        messageDiv.classList.add('error');
      }
    }

    function validateEmail(email) {
      // Simple RFC 5322 compliant regex for email validation
      const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      return re.test(email.toLowerCase());
    }

    form.addEventListener('submit', async (e) => {
      e.preventDefault();
      setMessage('', '');

      const email = emailInput.value.trim();

      if (!email) {
        setMessage('Please enter your email address.', 'error');
        emailInput.focus();
        return;
      }

      if (!validateEmail(email)) {
        setMessage('Please enter a valid email address.', 'error');
        emailInput.focus();
        return;
      }

      button.disabled = true;
      button.setAttribute('aria-busy', 'true');
      setMessage('Submitting...', '');

      try {
        // Simulate integration with email marketing service
        // Replace this fetch with your actual API call
        const response = await fetch(API_ENDPOINT, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ email })
        });

        if (!response.ok) {
          // Try to parse error message from response
          let errorMsg = 'Subscription failed. Please try again later.';
          try {
            const data = await response.json();
            if (data && data.error) errorMsg = data.error;
          } catch {}

          throw new Error(errorMsg);
        }

        setMessage('Thank you for subscribing! 🎉', 'success');
        form.reset();
      } catch (error) {
        setMessage(error.message, 'error');
      } finally {
        button.disabled = false;
        button.setAttribute('aria-busy', 'false');
      }
    });
  })();
</script>
</body>
</html>
