    <p class="description">Subscribe now and get exclusive updates, tips, and offers delivered straight to your inbox.</p>
    <form id="emailForm" novalidate>
      <label for="email" class="sr-only">Email address</label>
      <input
        type="email"
        id="email"
        name="email"
        placeholder="Enter your email address"
        autocomplete="email"
        required
        aria-describedby="error-msg"
      />
      <div id="error-msg" class="error-message" role="alert"></div>
      <button type="submit" aria-label="Subscribe to newsletter">Get Started →</button>
    </form>
    <p class="success-message" role="alert" aria-live="polite"></p>
  </main>

<script>
  (function() {
    const form = document.getElementById('emailForm');
    const emailInput = form.email;
    const errorMsg = document.getElementById('error-msg');
    const successMsg = document.querySelector('.success-message');

    function validateEmail(email) {
      // Simple email regex (RFC 5322 simplified)
      const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      return re.test(email.trim());
    }

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      errorMsg.style.display = 'none';
      successMsg.style.display = 'none';
      const emailValue = emailInput.value;

      if (!emailValue) {
        errorMsg.textContent = 'Please enter your email address.';
        errorMsg.style.display = 'block';
        emailInput.focus();
        return;
      }
      if (!validateEmail(emailValue)) {
        errorMsg.textContent = 'Please enter a valid email address.';
        errorMsg.style.display = 'block';
        emailInput.focus();
        return;
      }

      // Simulate successful submission
      form.reset();
      successMsg.textContent = 'Thank you for subscribing! 🎉';
      successMsg.style.display = 'block';
      emailInput.focus();
    });
  })();
</script>
</body>
</html>
