  <form id="testForm" novalidate>
    <label for="name">Full Name</label>
    <input type="text" id="name" name="name" placeholder="Your full name" required autocomplete="name" />

    <label for="email" style="margin-top:1.25rem;">Email Address</label>
    <input type="email" id="email" name="email" placeholder="you@example.com" required autocomplete="email" />

    <button type="submit" aria-live="polite">Get My Coupon</button>
    <div class="message" id="formMessage" role="alert" aria-live="assertive"></div>
  </form>
</main>
