<script>
  (function() {
    const form = document.getElementById('testForm');
    const messageEl = document.getElementById('formMessage');

    function validateEmail(email) {
      // Simple email regex for demonstration
      const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      return re.test(email.toLowerCase());
    }

    function showMessage(text, type = 'success') {
      messageEl.textContent = text;
      messageEl.className = 'message ' + type;
      messageEl.style.display = 'block';
    }

    function clearMessage() {
      messageEl.textContent = '';
      messageEl.className = 'message';
      messageEl.style.display = 'none';
    }

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      clearMessage();

      const name = form.name.value.trim();
      const email = form.email.value.trim();

      if (!name) {
        showMessage('Please enter your full name.', 'error');
        form.name.focus();
        return;
      }
      if (!email) {
        showMessage('Please enter your email address.', 'error');
        form.email.focus();
        return;
      }
      if (!validateEmail(email)) {
        showMessage('Please enter a valid email address.', 'error');
        form.email.focus();
        return;
      }

      // Disable button to prevent multiple submissions
      const submitBtn = form.querySelector('button[type="submit"]');
      submitBtn.disabled = true;
      submitBtn.textContent = 'Processing...';

      // Simulate async coupon delivery (e.g. API call)
      setTimeout(() => {
        // For testing, we just show a success message with a coupon code
        const couponCode = generateCouponCode();
        showMessage(`Thank you, ${name}! Your coupon code is: ${couponCode}`, 'success');
        submitBtn.disabled = false;
        submitBtn.textContent = 'Get My Coupon';
        form.reset();
      }, 1500);
    });

    function generateCouponCode() {
      // Generate a random 8-character alphanumeric coupon code
      const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
      let code = '';
      for (let i = 0; i < 8; i++) {
        code += chars.charAt(Math.floor(Math.random() * chars.length));
      }
      return code;
    }
  })();
</script>
</body>
</html>
