    <p>Stay updated with the latest news and exclusive offers.</p>
    <form id="emailCaptureForm">
        <input type="email" id="email" name="email" placeholder="Enter your email" required>
        <button type="submit">Subscribe</button>
    </form>
    <div class="success-message" id="successMessage">Thank you for subscribing!</div>
</div>

<script>
    document.getElementById('emailCaptureForm').addEventListener('submit', function(event) {
        event.preventDefault();
        var email = document.getElementById('email').value;
        if (validateEmail(email)) {
            document.getElementById('successMessage').style.display = 'block';
            // Here you would typically send the email to your email marketing system
            console.log('Email submitted:', email);
        } else {
            alert('Please enter a valid email address.');
        }
    });

    function validateEmail(email) {
        var re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return re.test(String(email).toLowerCase());
    }
</script>

</body>
</html>
