  <main class="container" role="main" aria-label="Email capture form">
    <h1>Develop Email Capture Form</h1>
    <p class="description">Implement a form to collect visitor email addresses securely.</p>
    <form id="emailForm" novalidate>
      <label for="emailInput" class="sr-only">Email address</label>
      <input
        type="email"
        id="emailInput"
        name="email"
        placeholder="Enter your email address"
        autocomplete="email"
        required
        aria-required="true"
        aria-describedby="emailHelp"
      />
      <button type="submit" id="submitBtn" aria-live="polite" aria-busy="false">Subscribe</button>
    </form>
    <div id="message" class="message" role="alert" aria-live="assertive"></div>
  </main>

<script>
  (function() {
    const form = document.getElementById('emailForm');
    const emailInput = document.getElementById('emailInput');
    const submitBtn = document.getElementById('submitBtn');
    const messageBox = document.getElementById('message');

    // Simple email regex for validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    function showMessage(text, type = 'success') {
      messageBox.textContent = text;
      messageBox.className = 'message ' + type;
      messageBox.style.display = 'block';
    }

    function clearMessage() {
      messageBox.textContent = '';
      messageBox.style.display = 'none';
      messageBox.className = 'message';
    }

    function setLoading(isLoading) {
      if (isLoading) {
        submitBtn.disabled = true;
        submitBtn.textContent = 'Submitting...';
        submitBtn.setAttribute('aria-busy', 'true');
      } else {
        submitBtn.disabled = false;
        submitBtn.textContent = 'Subscribe';
        submitBtn.setAttribute('aria-busy', 'false');
      }
    }

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      clearMessage();

      const email = emailInput.value.trim();

      if (!email) {
        showMessage('Please enter your email address.', 'error');
        emailInput.focus();
        return;
      }

      if (!emailRegex.test(email)) {
        showMessage('Please enter a valid email address.', 'error');
        emailInput.focus();
        return;
      }

      setLoading(true);

      // Simulate secure submission (e.g., to backend or API)
      // Here we just simulate with a timeout
      setTimeout(() => {
        setLoading(false);
        showMessage('Thank you for subscribing!', 'success');
        form.reset();
        emailInput.focus();
      }, 1500);
    });

    // Accessibility: hide label visually but keep for screen readers
