    <form id="coupon-form" novalidate>
      <label for="email" class="sr-only">Email address</label>
      <input
        type="email"
        id="email"
        name="email"
        placeholder="Enter your email address"
        autocomplete="email"
        required
        aria-required="true"
        aria-describedby="emailHelp"
      />
