  (function() {
    const form = document.getElementById('coupon-form');
    const emailInput = form.email;
    const messageEl = document.getElementById('message');

    // Simple email regex for validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    function showMessage(text, isError = false) {
      messageEl.textContent = text;
      messageEl.style.display = 'block';
      if (isError) {
        messageEl.classList.add('error');
      } else {
        messageEl.classList.remove('error');
      }
    }

    function generateCouponCode() {
      // Format: SAVE20-XXXX where XXXX is random alphanumeric uppercase 4 chars
      const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
      let code = '';
      for (let i = 0; i < 4; i++) {
        code += chars.charAt(Math.floor(Math.random() * chars.length));
      }
      return `SAVE20-${code}`;
    }

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      const email = emailInput.value.trim();

      if (!email) {
        showMessage('Please enter your email address.', true);
        emailInput.focus();
        return;
      }

      if (!emailRegex.test(email)) {
        showMessage('Please enter a valid email address.', true);
        emailInput.focus();
        return;
      }

      // Disable form while "processing"
      emailInput.disabled = true;
      form.querySelector('button').disabled = true;
      showMessage('Processing your request...');

      // Simulate async submission (e.g. API call)
      setTimeout(() => {
        const coupon = generateCouponCode();
        showMessage(`Thank you! Your 20% discount coupon code is: `);
        // Create a span with coupon code styled
        const couponSpan = document.createElement('span');
        couponSpan.textContent = coupon;
        couponSpan.style.background = '#764ba2';
        couponSpan.style.padding = '4px 10px';
        couponSpan.style.borderRadius = '6px';
        couponSpan.style.marginLeft = '6px';
        couponSpan.style.userSelect = 'all';
        couponSpan.style.fontFamily = "'Courier New', Courier, monospace";
        couponSpan.style.fontWeight = '700';
        couponSpan.style.color = '#fff';

        // Clear previous content and append text + coupon
        messageEl.textContent = '';
        messageEl.appendChild(document.createTextNode('Thank you! Your 20% discount coupon code is: '));
        messageEl.appendChild(couponSpan);

        // Reset form for another submission
        emailInput.value = '';
        emailInput.disabled = false;
        form.querySelector('button').disabled = false;
        emailInput.focus();
      }, 1500);
    });
  })();
</script>
</body>
